mapper.select:
SELECT COUNT(*)
FROM business
WHERE businessNum = #{businessNum}

mapper.update:
UPDATE business


        title = #{title}


        ,contents = #{contents}


        ,closeTime = #{closeTime}


        ,openTime = #{openTime}


WHERE businessId = #{businessId} and userId = #{userId}

mapper.select:
SELECT COUNT(*)
FROM business_phone
WHERE businessId = #{businessId} AND phone = #{phone}

mapper.insert:
insert into business_phone
set businessId = #{businessId},
phone = #{phone}

mapper.update:
update business_pic
set state = 1
where businessId = #{businessId}

update business
set state = #{state}
where businessId = #{businessId}

 update business

set  logo = #{logo}

 where businessId = #{businessId}

 update business

set paper = #{paper}

 where businessId = #{businessId}

mapper.select:
SELECT k.categoryId, k.categoryName, d.detailTypeId, d.detailTypeName, COALESCE(GROUP_CONCAT(DISTINCT IFNULL(c.pic, 'default.jpg') ORDER BY c.pic SEPARATOR ','), 'default.jpg') AS pic, b.businessId, b.businessName, b.title, ROUND(COALESCE(AVG(r.score), 0), 2) AS scoreAvg, p.price AS price, IFNULL(l.userId, 0) AS `like`, COUNT(DISTINCT r.reviewId) AS reviewCount, COUNT(s.serviceId) AS orderCount, g.regionId, g.name as region FROM business b LEFT JOIN detail_type d ON d.detailTypeId = b.detailTypeId LEFT JOIN category k ON k.categoryId = d.categoryId LEFT JOIN business_pic c ON c.businessId = b.businessId LEFT JOIN product p ON b.businessId = p.businessId LEFT JOIN service s ON s.productId = p.productId LEFT JOIN review r ON r.serviceId = s.serviceId LEFT JOIN `like` l ON l.businessId = b.businessId AND l.userId = #{signedUserId} left join business_region br on b.businessId = br.businessId left join region g on g.regionId = br.regionId WHERE 1=1
AND br.regionId = #{regionId}
AND k.categoryId = #{categoryId}
AND d.detailTypeId = #{detailTypeId}
GROUP BY b.businessId
ORDER BY b.busiCreatedAt DESC
ORDER BY scoreAvg DESC, reviewCount DESC
ORDER BY orderCount DESC
ORDER BY scoreAvg DESC, reviewCount DESC

select p.businessPicId, p.pic
from business_pic p
where p.businessId = #{businessId}
