mapper.select:
SELECT COUNT(*)
FROM business
WHERE businessNum = #{businessNum}

SELECT b.userId
FROM business b
WHERE b.businessId = #{businessId}

mapper.update:
UPDATE business


        title = #{title}


        ,contents = #{contents}


        ,closingTime = #{closingTime}


        ,openingTime = #{openingTime}


WHERE businessId = #{businessId} and userId = #{signedUserId}

mapper.select:
SELECT COUNT(*)
FROM business_phone
WHERE businessId = #{businessId} AND phone = #{phone}

mapper.insert:
insert into business_phone
set businessId = #{businessId},
phone = #{phone}

mapper.update:
update business_pic
set state = 1
where businessId = #{businessId}

update business
set state = #{state}
where businessId = #{businessId}

 update business

set  logo = #{logo}

 where businessId = #{businessId}

 update business

set paper = #{paper}

 where businessId = #{businessId}

mapper.select:
select  p.businessId, p.businessPicId, p.pic
from business_pic p
where p.businessId = #{businessId}
