mapper.select:
SELECT price AS etcPrice, `comment` AS etcComment, etcId
FROM etc
WHERE serviceId = #{serviceId}

mapper.update:
update service
set tid = #{tid}
where serviceId = #{serviceId}

update service
totalPrice = #{totalPrice}, addComment = #{addComment}, pyeong = #{pyeong}, completed = 2
where serviceId = #{serviceId}

update service_detail

     startDate = #{startDate}, 
     endDate = #{endDate}, 
     mStartTime = #{mStartTime}, 
     mEndTime = #{mEndTime} 

where serviceId = #{serviceId}

foreach.if:
insert into etc
set
serviceId = #{serviceId}
, price = #{item.etcPrice}
, `comment` = #{item.etcComment}

